package in.com.batch;

public class While_Loop_2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub 
            int i=1;
            while(i<=20) {
            	if(i%2==0) {
            		System.out.println("even numbers  list"+i);
            		
            	}
            	else {
            		System.out.println("odd number list"+i);
            	}
            	i++;
          
            }
	}

}
